mapper.select:
SELECT * FROM (
SELECT
t.*,
RANK() OVER (ORDER BY t.read_count DESC, t.id DESC) as `rank`
FROM gh_nrgl_bszn t
WHERE t.deleted = 0

    AND t.sxmc LIKE CONCAT('%', #{reqVO.sxmc}, '%')


    AND t.status = #{reqVO.status}


    AND t.ywfl = #{reqVO.ywfl}



AND (


    (
    t.dept_id = #{loginDeptId}
    OR (
    t.dept_id IN

        #{item}

    AND t.dept_id != #{loginDeptId}
    AND t.kjfw IN (1, 2)
    )
    )




    (
    t.dept_id = #{reqVO.deptId}
    OR (
    t.dept_id IN

        #{item}

    AND t.dept_id != #{reqVO.deptId}
    AND t.kjfw IN (1, 2)
    )
    )
    AND t.status = 2 

)
) temp_table
ORDER BY sort DESC
